package productdtodao;

import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import java.sql.Statement;
import java.util.ArrayList;
import java.util.List;
//import jdbcprogrms.product;

public class productdao {
	
	public Connection getConnection() {
			try {
				Class.forName("com.mysql.cj.jdbc.Driver");
				return DriverManager.getConnection("jdbc:mysql:///warehouse","root","root");
			} catch (ClassNotFoundException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			} catch (SQLException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
			return null;
		
	}
	public int insertData(Product p) throws SQLException {
		Connection con =getConnection();
		PreparedStatement pst =con.prepareStatement("insert into product values (?,?,?,?)");
		pst.setInt(1,p.getId());
		pst.setString(2, p.getBrand());
		pst.setDouble(3,p.getCost());
		pst.setDouble(4,p.getDiscount());
		return pst.executeUpdate();
	}
	     
	public int deleteData (int id) throws SQLException {
		Connection con=getConnection();
		PreparedStatement pst =con.prepareStatement("delete from product where id=?");
		pst.setInt(1, id);
		return pst.executeUpdate();		
	}
	
	public Product getById(int id) throws SQLException{
	     Connection con=getConnection();
	     PreparedStatement pst =con.prepareStatement("select* from product where id =?");
	     pst.setInt(1,id);
	     ResultSet rs =pst.executeQuery();
	     if(rs.next()) {
	    	 return new Product(rs.getInt(1),rs.getString(2),rs.getDouble(3),rs.getDouble(4));
	    	 
	     }
		return null;
	}
	
	public  List<Product> getAllProducts() throws SQLException{
		Connection con =getConnection();
		Statement st =con.createStatement();
		ResultSet rs =st.executeQuery("select * from product");
		List<Product> products = new ArrayList<Product>();
		while (rs.next()) {
			
			Product p= new Product(rs.getInt(1),rs.getString(2),rs.getDouble(3),rs.getDouble(4));
			products.add(p);
		}
		return products;
	}
	
	public int updateProduct(Product p ,int id)throws SQLException {
		Connection con =getConnection();
		PreparedStatement pst =con.prepareStatement("update product set brand=? ,cost=?,discount=? where id =?");
		pst.setString(1,p.getBrand());
		pst.setDouble(2, p.getCost());
		pst.setDouble(3,p.getDiscount());
		pst.setInt(4,id);
		return pst.executeUpdate();
		}
}









































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































		